sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
	if(g_form.isNewRecord()){
		var patron = new GlideAjax('Limits');
		patron.addParam('sysparm_name','PatronTotal');
		patron.getXML(populateListField2);

		function populateListField2(response){
			var answer2 = response.responseXML.documentElement.getAttribute("answer");
			if(answer2>=2){
				block();
			}
		}
	}else{
		if(g_user.hasRole('x_898711_library_m.librarian')){
			//hide from librarians
			g_form.removeOption('approval', 'approved');
			g_form.removeOption('approval', 'rejected');

			var librarian = new GlideAjax('Limits');
			librarian.addParam('sysparm_name','LibrarianLimit');
			librarian.getXML(populateListField);

			function populateListField(response){
				var answer = response.responseXML.documentElement.getAttribute("answer");
				if(answer>=5 && g_form.getValue('assigned_to') != g_user.userID){
					block();
				}
			}
		}else{
			//hide from patrons		
			g_form.removeOption('approval', 'requested');
		}
	}
	// 	if(g_form.getValue('u_field_name_2')=='USD;0' || g_form.getValue('u_field_name_2')=='USD;0.00'){
	// 		g_form.clearValue('u_field_name_2');
	// 		g_form.setValue('u_field_name_2', 'PHP;0');
	// 	}else{
	// 		alert(g_form.getValue('u_field_name_2'));
	// 	}
// 	}
	function block(){
		var fields = g_form.getEditableFields();
		for (var x = 0; x < fields.length; x++) {
			g_form.setReadOnly(fields[x], true);
		}
		g_form.setMandatory('borrowed_book', false);
		g_form.setVisible('borrowed_book', false);
	}
}
