sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	if(g_form.getValue('assigned_to')==='' && (g_form.getValue('approval')==='requested' && g_user.hasRole('x_898711_library_m.librarian')) || (g_form.getValue('approval')==='approved' && g_user.hasRole('x_898711_library_m.head_librarian'))){
		g_form.setValue('assigned_to', g_user.userID, g_user.getFullName());
	}
}
