sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   if(g_form.getValue('approval')==='approved' && g_user.hasRole('x_898711_library_m.head_librarian')){
		g_form.setValue('state', 2, 'Borrowing');

		var BookDates = new GlideAjax('BookDates');
		BookDates.addParam('sysparm_name','getStartDate');
		BookDates.getXML(populateListField);

		var BookDates2 = new GlideAjax('BookDates');
		BookDates2.addParam('sysparm_name','getDueDate');
		BookDates2.getXML(populateListField2);

		function populateListField(response){
			var date = response.responseXML.documentElement.getAttribute("answer");
			g_form.setValue('work_start', date, date);
		}

		function populateListField2(response){
			var date = response.responseXML.documentElement.getAttribute("answer");
			g_form.setValue('due_date', date, date);
		}
	}
}
