sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
// 	current.approval=="approved" && previous.approval!="approved"
	var array = current.borrowed_book.split(",");
	var rej = false;
	var list=[];
	for(var i=0; i<array.length;i++){ //check availability
		var books = new GlideRecord('x_898711_library_m_books');
		books.addQuery('sys_id', array[i]);
		books.query();
		while(books.next()){
			if(books.available_copies==0 || books.status=='deleted'){
				rej=true;
				list.push(books.title);
			}
		}
	}
	if(rej==true){
		gs.info("Transaction " +current.number+ " attempt borrow - need to reject");
		var present = new GlideRecord('x_898711_library_m_book_transaction');
		present.addQuery('number', current.number);
		present.query();
		present.next();

		present.approval='rejected';
		present.state=4;
		present.short_description="System has rejected this transaction. Unavailable book/s: " + list.join(", "); 
		present.update();
		gs.info("Num: " +current.number+"\nDesc: System has rejected this transaction. Unavailable book/s: " + list.join(", "));

	}else{		
		var present2 = new GlideRecord('x_898711_library_m_book_transaction');
		present2.addQuery('number', current.number);
		present2.query();
		present2.next();

		gdt= new GlideDateTime();
		gdt2= new GlideDateTime();
		gdt2.addDaysLocalTime(7);

		present2.work_start = gdt;
		present2.due_date = gdt2;
// 		present.approval='approved';
		present2.short_description="Head Librarian has approved this transaction";
		present2.state=2;

		present2.update();

		gs.info("Approved: Transaction " +present2.number);

		for(i=0; i<array.length;i++){
			books = new GlideRecord('x_898711_library_m_books');
			books.addQuery('sys_id', array[i]);
			books.query();
			while(books.next()){
				books.setValue('available_copies',(books.available_copies-1));
				books.update();
				gs.info("Borrowed book: " +books.title+ ", updated to " +books.available_copies +" copies left");
			}
		}
	}
})(current, previous);
