sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

          // Add your code here
		var total = new Limits().PatronTotalforEmail(current.u_field_name_1);
		var overdues = new GlideRecord('x_898711_library_m_book_transaction');
		overdues.addQuery('due_date', '<=', new GlideDateTime());
		overdues.addQuery('u_field_name_1', current.u_field_name_1);
		overdues.addEncodedQuery('state=2');
		overdues.addNotNullQuery('due_date');
		overdues.query();

		template.print("<p style=\"font:18px Verdana, sans-serif\">Your current overdue fee is $"+total+" USD.");
		if(overdues.getRowCount()>1)
			template.print("<br>You also have " +(overdues.getRowCount()-1)+" other overdue transactions.<br>");
		if(total>2)
			template.print("<br>While your overdue fee is greater than $2.00 USD, you will not be able to borrow books.");
		else
			template.print("<br>");
		template.print("<br>Please return the books as soon as possible to close this transaction.");
		if(total<2)
			template.print("<br>Otherwise, when your overdue fees total to $2.00 USD or greater, you will no longer be able to borrow books.");
		template.print("</p>");
})(current, template, email, email_action, event);
