sys_script_include.script:
GetBorrowedBooks = Class.create();
GetBorrowedBooks.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getBooks: function() {
		var bookRecord=new GlideRecord('x_898711_library_m_books');
		bookRecord.get(this.getParameter('sysparm_sys_id'));
		return bookRecord.title + '';
	},
	hasBorrow: function(bookTitle){
		var borrow = new GlideRecord('x_898711_library_m_book_transaction');
		borrow.addQuery('borrowed_book', 'CONTAINS', bookTitle);
		borrow.addEncodedQuery('state=-5^ORstate=2');
		borrow.query();
		return borrow.getRowCount();
	},
	sort: function(){
		var sorted = {};
		sorted.id = [];
		sorted.title = [];
		var books = new GlideRecord('x_898711_library_m_books');
		books.addQuery('sys_id', 'IN', this.getParameter('sysparm_booklist'));
		books.orderBy('title');
		books.query();
// 		gs.info("#Books: "+books.getRowCount());
		while(books.next()){
// 			gs.info("    Book: " +books.title);
			sorted.id.push(books.sys_id+"");
			sorted.title.push(books.title+"");
		}
		sorted.count=books.getRowCount();
// 		gs.info("Sorted: " +sorted.title);
		return JSON.stringify(sorted);
	},
    type:'GetBorrowedBooks'
});
