sys_script_include.script:
var Limits = Class.create();
Limits.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    PatronTotal: function() {
		var books = new GlideAggregate('x_898711_library_m_book_transaction');
		books.groupBy('u_field_name_1');
		books.addQuery('u_field_name_1', gs.getUserID());
		books.addEncodedQuery('state=-5^ORstate=2');
		books.addAggregate('SUM', 'u_field_name_2');
		books.query();
		books.next();
		return parseFloat(books.getAggregate('SUM', 'u_field_name_2')).toFixed(2);
    },
	LibrarianLimit: function (){
		var transactions = new GlideAggregate('x_898711_library_m_book_transaction');
		transactions.addQuery('assigned_to', gs.getUserID());
		transactions.addEncodedQuery('state=-5^ORstate=2');
		transactions.addAggregate('COUNT', 'assigned_to');
		transactions.query();
		transactions.next();
		return transactions.getAggregate('COUNT', 'assigned_to');
	},
	PatronTotalforEmail: function(currUser){
		var books = new GlideAggregate('x_898711_library_m_book_transaction');
		books.groupBy('u_field_name_1');
		books.addQuery('u_field_name_1', currUser);
		books.addEncodedQuery('state=-5^ORstate=2');
		books.addAggregate('SUM', 'u_field_name_2');
		books.query();
		books.next();
		return parseFloat(books.getAggregate('SUM', 'u_field_name_2')).toFixed(2);
	},
// 	UpdateBlock: function (current){
// 		return (current.getTableName()==('x_898711_library_m_book_transaction') && (g_form.getValue('assigned_to')=='' || g_form.getValue('assigned_to')==g_user.userID));
// 	},
    type: 'Limits'
});
