sysauto_script.script:
var commas = new GlideRecord('x_898711_library_m_book_transaction');

// commas.addQuery('borrowed_books', 'CONTAINS', ',');
// commas.addNotNullQuery('borrowed_books');
commas.query();
var none=0;
var almost=0;
var updated=0;
var sorted = {};
sorted.id = [];
sorted.title = [];
while(commas.next()){
// 	gs.info(commas.borrowed_book +"\n" +commas.number);
	if(commas.borrowed_book.split(',').length>1){
// 		gs.info("list: " +commas.borrowed_book+ "\nNumber: " +commas.number);
// 		gs.info("list: " +commas.borrowed_book+ "\nSplit: " +commas.borrowed_book.split(',').length+ "\nNum: " +commas.number);
		var books = new GlideRecord('x_898711_library_m_books');
		books.addQuery('sys_id', 'IN', commas.borrowed_book);
		books.orderBy('title');
		books.query();		
		sorted.id = [];
		sorted.title = [];
		var list= commas.borrowed_book+'';
		while(books.next()){
			sorted.id.push(books.sys_id+"");
			sorted.title.push(books.title+"");
		}
		sorted.count=books.getRowCount();
		if(list!=sorted.id){
// 			gs.info("Updated\n#Books: "+books.getRowCount()+ "\nList: " +list+ "\nSorted: " +sorted.id);
			commas.setValue('borrowed_book', sorted.id);
			commas.update();
			updated++;
		}else{
			almost++;
		}
	}else{
		none++;
	}
}
gs.info("None: "+none+ "\nAlmost: "+almost+ "\nUpdate: "+updated);
